import React from "react";
const Comment = props => {
  return (
    <div className="mt-3">
      <div className="card card-outline-secondard">
        <div className="card-block">
          <h3>
            <i class=" fas fa-user " />
            {props.head}
          </h3>
          <p>{props.body}</p>
          {props.delete && (
            <button
              className="btn btn-danger float-right"
              onClick={props.deleteComment}
            >
              Delete
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Comment;
