import React, { Component } from "react";
import quizQuestions from "./quizQuestions";
import Quiz from "./Quiz";
import Result from "./Result";
import Navbar from "../Layout/Navbar";
import { NavLink, Link } from "react-router-dom";
import Fab from "@material-ui/core/Fab";
import Button from "@material-ui/core/Button";
import MenuBar from "../Layout/MenuBar";
import SignedInLinks from "../Dashboard/SignedInLinks";
import "../Layout/Navbar.css";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import img from "../image/eos6d.jpg";
import img2 from "../image/eos5dsr.jpg";
import img3 from "../image/k1.jpg";
import img5 from "../image/d5.png";
import img6 from "../image/markIV.jpg";

const styles = theme => ({
  root: {
    ...theme.mixins.gutters(),
    paddingTop: theme.spacing.unit * 2,
    paddingBottom: theme.spacing.unit * 2,
    width: "65%",
    marginLeft: "10%",
    marginTop: "1%",
    backgroundColor: "#f8f6f7",
    borderRadius: "5px",
    display: " inline-block"
  },
  header: {
    fontSize: "25px",
    color: "rgb(115, 70, 124)",
    alignItems: "center",
    fontFamily: "'Montserrat', sans-serif"
  },
  question: {
    marginTop: "5%",
    fontSize: 17,
    alignItems: "center",
    fontFamily: "'Montserrat', sans-serif"
  },
  menu: {
    backgroundColor: " rgb(115, 70, 124)",
    cursor: "pointer"
  },
  fab: {
    backgroundColor: " rgb(115, 70, 124)",
    fontSize: 20,
    color: "white",
    alignItems: "center"
  },
  part: {
    padding: "3%",
    border: "1px solid rgb(115, 70, 124)"
  },
  tag: {
    fontSize: "12px",
    marginLeft: "50%"
  }
});
class QuizHome extends Component {
  constructor(props) {
    super(props);

    this.state = {
      anchorEl: null,
      counter: 0,
      questionId: 1,
      question: "",
      answerOptions: [],
      answer: "",
      answersCount: {
        Profesionalac: 0,
        Amater: 0,
        Početnik: 0,
        Kompaktni: 0
      },
      result: "",
      show: [false, false, false, false, false]
    };

    this.handleAnswerSelected = this.handleAnswerSelected.bind(this);
  }

  componentWillMount() {
    const shuffledAnswerOptions = quizQuestions.map(question =>
      this.shuffleArray(question.answers)
    );
    this.setState({
      question: quizQuestions[0].question,
      answerOptions: shuffledAnswerOptions[0]
    });
  }

  shuffleArray(array) {
    var currentIndex = array.length,
      temporaryValue,
      randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {
      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  }

  handleAnswerSelected(event) {
    this.setUserAnswer(event.currentTarget.value);

    if (this.state.questionId < quizQuestions.length) {
      setTimeout(() => this.setNextQuestion(), 300);
    } else {
      setTimeout(() => this.setResults(this.getResults()), 300);
    }
  }

  setUserAnswer(answer) {
    this.setState((state, props) => ({
      answersCount: {
        ...state.answersCount,
        [answer]: state.answersCount[answer] + 1
      },
      answer: answer
    }));
  }

  setNextQuestion() {
    const counter = this.state.counter + 1;
    const questionId = this.state.questionId + 1;

    this.setState({
      counter: counter,
      questionId: questionId,
      question: quizQuestions[counter].question,
      answerOptions: quizQuestions[counter].answers,
      answer: ""
    });
  }

  getResults() {
    const answersCount = this.state.answersCount;
    const answersCountKeys = Object.keys(answersCount);
    const answersCountValues = answersCountKeys.map(key => answersCount[key]);
    const maxAnswerCount = Math.max.apply(null, answersCountValues);

    return answersCountKeys.filter(key => answersCount[key] === maxAnswerCount);
  }

  setResults(result) {
    if (result.length === 1) {
      console.log(this.state);
      this.setState({ result: result[0] });
    } else {
      this.setState({ result: "Ti si osoba koju sve privači!" });
    }
  }

  renderQuiz() {
    return (
      <Quiz
        answer={this.state.answer}
        answerOptions={this.state.answerOptions}
        questionId={this.state.questionId}
        question={this.state.question}
        questionTotal={quizQuestions.length}
        onAnswerSelected={this.handleAnswerSelected}
      />
    );
  }
  show() {}
  renderResult() {
    console.log(this.state);
    if (this.state.result === "Profesionalac") {
      console.log("true profesionalac");
      return (
        <div>
          Ti si profesionalac
          <Card className={classes.part}>
            <p>
              Pravo je vrijeme da si priuštite fotoaparat kojeg ste oduvijek
              željeli - full-frame DSLR s odličnom tehnikom snimanja, izvrsnom
              kontrolom nad ISO i nad najmanjim detaljima. To je ono što pravi
              profesionalci koriste. Čestitamo, uspjeli ste!
            </p>
            <p>
              Ako se smatrate profesionalnim fotografom, odnosno živite od
              snimanja i prodaje snimljenih fotografija, tada svaka uložena kuna
              u ove uređaje vrijedi. I zato Vam trebaju full-frame DSLR
              fotoaparati jer su oni najbolji među najboljima.
            </p>
            <p>
              Ukratko opisano , full-frame DSLR fotoaparati (Digital Single-Lens
              Reflex) su oni koji koriste senzor slike koji je ekvivalent
              35-milimetarskom formatu, odnosno 36 x 24 milimetarskom filmu.
              Radi se o digitalcima s vrlo moćnim karakteristikama, te u
              nastavku su navedeni neke od najboljih koje trenutno možete
              kupiti.
            </p>
            <p>
              U nastavku su preporuke za popularnije profesionalne DSLR-ove.
            </p>
            <h4>
              <b>Canon EOS 6D</b>
            </h4>

            <p>
              <ul>
                <li>Senzor punog kadra s 20,2 megapiksela </li>
                <li>Izdržljiva, lagana konstrukcija</li>
                <li>
                  Maksimalna ISO vrijednost 25 600 (može se proširiti do ISO 102
                  400)
                </li>
                <li>AF u 11 točaka osjetljiv do -3 EV</li>
                <li>GPS* bilježi vašu lokaciju </li>
                <li>Wi-Fi** prijenos datoteka i daljinska kontrola</li>
                <li>Full HD videozapisi</li>
              </ul>
            </p>
            <p>
              Novi Canon EOS 6D je vjerojatno jedan od najisplativijih
              full-frame DSLR fotoaparata na tržištu. Ovo je zaista vrhunski
              digitalac koji maksimalno iskorištava svoje mogućnosti. Mnogi ga
              uspoređuju si modelom EOS 5D Mark III. Inače je i jedan od
              najlakših digitalaca u klasi, pa ako namjeravate snimati puno,
              možda i po cijeli dan, vaš bi izbor mogao pasti na EOS 6D.
            </p>
            <img className={classes.ima1} src={img} alt="img" />
            <p className={classes.tag}>
              Photo by Oscar Ivan Esquivel Arteaga on Unsplash
            </p>
            <h4>
              <b>Canon EOS 5DS R</b>
            </h4>
            <p>
              <ul>
                <li>CMOS senzor s 50,6 megapiksela</li>
                <li>Dva procesora DIGIC 6, do 5 sličica u sekundi</li>
                <li>Senzor za RGB+IR mjerenje sa 150 000 piksela</li>
                <li>AF u 61 točki</li>
                <li>Snimanje uz obrezivanje, USB 3.0 </li>
                <li>Sustav za kontrolu vibracija zrcala</li>
                <li>Snimanje s mjeračem intervala</li>
              </ul>
            </p>
            <p>
              Najjača karakteristika ovog modela je njegova visora rezolucija od
              čak 50,6 megapiksela, tako da će vam u full-frame formatu ponuditi
              nevjerojatno detaljne fotografije. Posebno je efikasan u
              kontroliranim uvjetima, recimo u studiju za snimanje.
            </p>
            <img className={classes.ima1} src={img2} alt="img2" />
            <p className={classes.tag}>
              http://www.everyothershot.com/canon-5dsr-list-of-suitable-lenses/
            </p>
            <h4>
              <b>Pentax K1</b>
            </h4>
            <p>
              <ul>
                <li>CMOS senzor s 36,4 megapiksela</li>
                <li>Do 4,4 sličica u sekundi</li>
                <li>Snimanje u 1080p rezoluciji</li>
              </ul>
            </p>
            <p>
              {" "}
              Može se pohvaliti vrlo robusnim kućištem od magnezijske legure.
              Spomenimo i kako je Pentax K1 u vlasništvu Ricoha, te je vrlo
              jednostavan i intuitivan za korištenje što može zahvaliti i
              ergonomskom rukohvatu. Kažu i kako u ovom cjenovnom razredu radi
              daleko najbolje fotografije.
            </p>
            <img className={classes.ima1} src={img3} alt="img3" />
            <p className={classes.tag}>
              https://www.practicalphotography.com/group-tests-articles/2016/8/24/6-best-dslr-and-csc-cameras-with-super-high-resolutions
            </p>
            <h4>
              <b>Nikon D5</b>
            </h4>
            <p>
              <ul>
                <li>CMOS senzor s 20,8 megapiksela</li>
                <li>Do 12 sličica u sekundi</li>
                <li>Snimanje u 2160p rezoluciji</li>
              </ul>
            </p>
            <p>
              Ako ste najčešće u pokretu, ovo je digitalac koji bi trebali uzeti
              u obzir. Njegov 20,8 MP full-frame senzor ima auto-fokus u čak 153
              točke, a u sekundi može ispaliti čak 12 slika što je odlično za
              snimanje objeaka u pokretu. Kućište je veliko, čvrsto i s
              dvostrukim rukohvatom što dodatno olakšava snimanje. Omiljeni je
              digitalac sportskih fotografa. Možda nema neki veliki dinamički
              raspon kao konkurencija, ali zato ima odličan ISO raspon, a i može
              snimiti 4K video.
            </p>
            <img className={classes.ima1} src={img5} alt="img5" />
            <p className={classes.tag}>
              https://www.nikon.hr/hr_HR/product/digital-cameras/slr/professional/d5#tech_specs
            </p>
            <h4>
              <b>Canon EOS 5DS Mark IV</b>
            </h4>
            <p>
              <ul>
                <li>CMOS senzor s 30 megapiksela</li>
                <li>Dual Pixel CMOS AF, do 12 sličica u sekundi</li>
                <li>
                  Mikroprilagodba slike, pomak zamućenja pozadine, smanjenje
                  pojave dvostruke slike{" "}
                </li>
                <li>
                  AF do 61 točka / 21 križna AF točka (s mogućnošću pojedinačnog
                  odabira)
                </li>
                <li>
                  Full HD do 50/60p, HD do 100/120p HDR videozapis, videozapis s
                  ubrzanim prolaskom vremena
                </li>
              </ul>
            </p>
            <p>
              Možda jedan od najboljih sve-u-jednom digitalaca koji je odličan
              na svim područjima. Omiljen je kod svih vrsta fotografa, od
              amatera do profesionalaca. Ističe se po jedinstvenom Dual Pixel
              auto-fokusu, snimanjem 4K videa, a ekran je osjetljiv na dodir.
              Svidjet će vam se i podrška za Wi-Fi i GPS. Jednostavno svestran,
              tko ga ne bi volio.
            </p>
            <img className={classes.ima1} src={img6} alt="img6" />
          </Card>{" "}
        </div>
      );
    } else if (this.state.result === "Amater") {
      console.log("true amater");
      return <div>Amater</div>;
    } else if (this.state.result === "Početnik") {
      console.log("true početnik");
    } else if (this.state.result === "Kompaktni") {
      console.log("true kompaktni");
    } else {
      console.log("sth else");
    }
    return <Result quizResult={this.state.result} />;
  }
  changeName() {
    let text = "Prikaži više";
    text += this.state.show === true ? " " : " ";
    return text;
  }
  showHide(num) {
    this.setState(prevState => {
      const newItems = [...prevState.show];
      newItems[num] = !newItems[num];
      return { show: newItems };
    });
  }
  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    return (
      <div className="App">
        <Navbar />
        <div className="sticky-anchor" />
        <div className="sticky">
          <Link to="/quiz" className={classes.link}>
            <Button className="button1">Quiz</Button>
          </Link>{" "}
          <br />
          <Button
            className="button1"
            onClick={() => {
              this.props.logout();
            }}
          >
            Odjavi se
          </Button>{" "}
          <br />
        </div>
        <div className="sticky-small">
          <div className="dropdown">
            <Fab
              aria-label="Add"
              className={classes.fab}
              aria-owns={anchorEl ? "simple-menu" : undefined}
              aria-haspopup="true"
            >
              <i className="fas fa-camera" />
            </Fab>
            <div className="dropdown-content">
              <NavLink to="/quiz" className={classes.link}>
                Quiz
              </NavLink>

              <a
                onClick={() => {
                  this.props.logout();
                }}
                className={classes.menu}
              >
                Izađi
              </a>
            </div>
          </div>
        </div>

        <Paper className={classes.root} elevation={1}>
          <Typography variant="h5" component="h3" className={classes.header}>
            Saznaj kakav si fotograf
          </Typography>

          <Typography component="p" className={classes.question}>
            {" "}
            {this.state.result ? this.renderResult() : this.renderQuiz()}
          </Typography>
        </Paper>
      </div>
    );
  }
}
QuizHome.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(QuizHome);
