import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { getUser, getDbUsers } from "../Actions/UserActions";
import { getPosts } from "../Actions/PostActions";
import Loading from "../Components/Loading";

class LoadingComponent extends Component {
  componentWillMount() {
    const { userLoading, postsLoading } = this.props;
    if (userLoading === undefined) {
      this.props.getUser();
    }

    if (postsLoading === undefined) {
      this.props.getPosts();
    }
    if (dbUserLoading === undefined) {
      this.props.getDbUsers;
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.postsLoading === -1 && nextProps.user !== null) {
      this.props.getPosts();
    }
  }

  render() {
    const { userLoading, postsLoading, dbUserLoading, children } = this.props;
    if (
      (!userLoading && !postsLoading && !dbUserLoading) ||
      this.props.user === null
    ) {
      return <div>{children}</div>;
    } else {
      return <Loading />;
    }
  }
}

function mapStateToProps(state) {
  return {
    userLoading: state.loading.user,
    postsLoading: state.loading.posts,
    dbUserLoading: state.loading.dbUser,
    user: state.user
  };
}

export default withRouter(
  connect(
    mapStateToProps,
    { getUser, getDbUsers, getPosts }
  )(LoadingComponent)
);
