import React, { Component } from "react";
import InputField from "../Components/InputField";
import FooterFormButton from "../Components/FooterFormButton";
import SimpleBox from "../Components/SimpleBox";
import { createAccount } from "../Actions/UserActions";
import { connect } from "react-redux";
import ErrorAlert from "../Components/ErrorAlert";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
const styles = theme => ({
  card: {
    width: 490,
    height: 320,
    marginLeft: 140,
    backgroundColor: "#f7f7f7"
  },
  tit: {
    textDecoration: "none"
  },
  title: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif"
  },
  textField: {
    marginLeft: 30,
    width: 240,
    padding: 5
  },
  pos: {
    marginBottom: 12
  },
  reg: {
    marginLeft: 35
  },
  login: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
    marginLeft: 30,
    color: "#6a3499"
  },
  cssLabel: {
    "&$cssFocused": {
      color: "#6a3499",
      fontSize: 20
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: "#6a3499"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: "#6a3499"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 3
    }
  },
  bootstrapInput: {
    borderRadius: 6,
    backgroundColor: "#6a3499",
    border: "1px solid #6a3499",
    fontSize: 16,
    width: "600px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: "#6a3499",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 20
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "#c0bfcc",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none"
  },
  register: {
    fontSize: 15,
    fontFamily: "'Montserrat', sans-serif",
    color: "#6a3499"
  }
});

class CreateAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      error: ""
    };
  }

  isValid() {
    const { email, password, confirmPassword } = this.state;

    if (email === "" || password === "" || confirmPassword === "") {
      this.setState({
        error: "Please enter in all fields"
      });
      return false;
    }

    if (password !== confirmPassword) {
      this.setState({
        error: "Please make sure your passwords match"
      });
      return false;
    }

    return true;
  }

  submitAccount(event) {
    event.preventDefault();
    if (!this.isValid()) {
      return;
    }
    this.props
      .createAccount(this.state.email, this.state.password)
      .then(() => {
        this.props.history.replace("/");
      })
      .catch(err => {
        this.setState({
          error: err.message
        });
      });
  }

  renderBody() {
    const errStyle = {
      borderColor: "red"
    };
    const { classes } = this.props;
    return (
      <div>
        <form onSubmit={event => this.submitAccount(event)}>
          <TextField
            id="email"
            type="text"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
          />{" "}
          <br />
          <TextField
            id="password"
            type="password"
            label="Password"
            inputAction={event =>
              this.setState({ password: event.target.value })
            }
            style={this.state.error ? errStyle : null}
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
          />{" "}
          <br />
          <TextField
            id="confirm-password"
            type="password"
            label="Confirm Password"
            inputAction={event =>
              this.setState({ confirmPassword: event.target.value })
            }
            style={this.state.error ? errStyle : null}
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
          />{" "}
          <br />
          {this.state.error && <ErrorAlert>{this.state.error}</ErrorAlert>}
          <FooterFormButton
            submitLabel="Create Account"
            otherLabel="Go back"
            goToLink="/Login"
            {...this.props}
          />
        </form>
      </div>
    );
  }

  render() {
    return <SimpleBox body={this.renderBody()} title="Create Account" />;
  }
}
CreateAccount.propTypes = {
  classes: PropTypes.object.isRequired
};

export default connect(
  null,
  { createAccount }
)(withStyles(styles)(CreateAccount));
