import React, { Component } from "react";
import { connect } from "react-redux";
import { addProfile } from "../Actions/PostActions";
import { Field, reduxForm, reset } from "redux-form";
import _ from "lodash";
import PostCard from "../Components/PostCard";
import { getUser, logout } from "../Actions/UserActions";
import { Link } from "react-router-dom";
import { required } from "../Helpers/ReduxFormValidation";
import { auth } from "../Firebase";
const INITIAL_STATE = {
  username: "",
  email: "",
  password: "",
  phone: "",
  address: "",
  error: null
};
const byPropKey = (propertyName, value) => () => ({
  [propertyName]: value
});

class AddProfile extends Component {
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE };
  }
  onSubmit = event => {
    const { username, email, password, phone, address } = this.state;
    auth
      .doCreateUserWithEmailAndPassword(email, password)
      .then(authUser => {
        // Create a user in your own accessible Firebase Database too
        auth
          .doCreateUser(
            authUser.user.uid,
            username,
            email,
            password,
            phone,
            address
          )
          .then(() => {
            this.setState({ ...INITIAL_STATE });
          })
          .catch(error => {
            this.setState(byPropKey("error", error));
          });
      })
      .catch(error => {
        this.setState(byPropKey("error", error));
      });

    event.preventDefault();
  };
  render() {
    const { username, email, password, phone, address, error } = this.state;
    return (
      <div>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label className="control-label">User Name</label>
            <input
              className="form-control form-white"
              type="text"
              id="username"
              required
              placeholder="User Name"
              value={username}
              onChange={event =>
                this.setState(byPropKey("username", event.target.value))
              }
            />
            <input
              className="form-control form-white"
              type="text"
              id="email"
              required
              placeholder="email"
              value={email}
              onChange={event =>
                this.setState(byPropKey("email", event.target.value))
              }
            />
            <input
              className="form-control form-white"
              type="text"
              id="password"
              required
              placeholder="password"
              value={password}
              onChange={event =>
                this.setState(byPropKey("password", event.target.value))
              }
            />
          </div>{" "}
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary waves-effect"
              data-dismiss="modal"
            >
              Close
            </button>
            <button
              className="btn btn-block btn-gradient waves-effect waves-light"
              type="submit"
              disabled={isInvalid}
            >
              Create User
            </button>
            {error && <p>{error.message}</p>}
          </div>
        </form>
      </div>
    );
  }
}
export default AddProfile;
