import React, { Component } from "react";
import { connect } from "react-redux";
import { Field, reduxForm, reset } from "redux-form";
import { saveComment } from "../Actions/PostActions";
import { required } from "../Helpers/ReduxFormValidation";

class AddComment extends Component {
  state = {
    message: localStorage.getItem("message") || ""
  };
  onSubmit(values) {
    this.props
      .saveComment(values, this.props.id, this.props.uid, this.getUsername())
      .then(() => {
        this.props.dispatch(reset("CreateCommentForm"));
      });
  }

  renderComment(field) {
    const {
      meta: { touched, error }
    } = field;
    const errStyle = {
      borderColor: "red"
    };
    return (
      <div>
        <textarea
          className="field"
          placeholder="UpiÅ¡i svoj komentar..."
          style={touched && error ? errStyle : null}
          {...field.input}
        />
      </div>
    );
  }
  getUsername() {
    var mail = this.props.user.email;
    var username = mail.substring(0, mail.indexOf("@"));
    return username;
  }
  render() {
    const { handleSubmit, classes } = this.props;
    return (
      <form onSubmit={handleSubmit(this.onSubmit.bind(this))}>
        {this.getUsername()};
        <Field
          name="content"
          component={this.renderComment}
          validate={required}
        />
        <div className="d-flex justify-content-end mt-1">
          <button type="submit" className="btn-save">
            Spremi komentar
          </button>
        </div>
      </form>
    );
  }
}

export default reduxForm({
  form: "CreateCommentForm"
})(
  connect(
    (state, ownProps) => ({ uid: state.user.uid }),
    { saveComment }
  )(AddComment)
);
