import React, { Component } from "react";
import { connect } from "react-redux";
import { addProfile } from "../Actions/PostActions";
import { Field, reduxForm, reset } from "redux-form";
import _ from "lodash";
import PostCard from "../Components/PostCard";
import { getUser, logout } from "../Actions/UserActions";
import { Link } from "react-router-dom";
import { required } from "../Helpers/ReduxFormValidation";

class AddProfile extends Component {
  renderField(field) {
    const errStyle = {
      borderColor: "red"
    };
    return (
      <input
        type="text"
        placeholder={`Enter a ${field.label}...`}
        {...field.input}
        className={field.class}
        style={field.meta.touched && field.meta.error ? errStyle : null}
      />
    );
  }

  onSubmit(values) {
    this.props
      .addProfile(values, this.props.user.uid)
      .then(this.props.dispatch(reset("AddUser")));
  }

  render() {
    const { handleSubmit } = this.props;
    return (
      <div>
        <div className="navbar">
          <button
            className="btn btn-danger"
            onClick={() => {
              this.props.logout();
            }}
          >
            Sign out
          </button>
        </div>

        <div className="container">
          <div className="navbar fixed-bottom">
            <form
              onSubmit={handleSubmit(this.onSubmit.bind(this))}
              className="footerForm"
            >
              <Field
                name="fname"
                component={this.renderField}
                label="Name"
                class="footer-title"
                validate={required}
              />
              <Field
                name="lname"
                component={this.renderField}
                label="Last Name"
                class="footer-body"
              />

              <button type="submit" className="btn footer-button">
                Post
              </button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

let form = reduxForm({
  form: "NewPost"
})(AddProfile);

form = connect(
  (state, ownProps) => ({
    profile: state.profile,
    user: state.user
  }),
  { addProfile, logout }
)(form);

export default form;
