import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card'; 
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography'; 
import TextField from '@material-ui/core/TextField';
import Modal from '@material-ui/core/Modal';
function rand() {
    return Math.round(Math.random() * 20) - 10;
  }

  function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }
const styles = theme => ({
  card: {
     width: 490,
     height: 320,
     marginLeft: 140,
     backgroundColor: "#f7f7f7",

  }, 
   tit: {
       textDecoration: "none"
   },
  title: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
  },
  textField: {
    marginLeft: 30, 
    width: 240,
    padding: 5
  },
  pos: {
    marginBottom: 12,
  },
  reg: {
    marginLeft: 35
  },
  login: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
    marginLeft: 30, 
    color: "#6a3499"
  }, 
  cssLabel: {
    '&$cssFocused': {
      color: '#6a3499',
      fontSize: 20
    },
  },
  cssFocused: {},
  cssUnderline: {
    '&:after': {
      borderBottomColor: '#6a3499',
    },
  },
  cssOutlinedInput: {
    '&$cssFocused $notchedOutline': {
      borderColor: '#6a3499',
    },
  },
  notchedOutline: {},
  bootstrapRoot: {
    'label + &': {
      marginTop: theme.spacing.unit * 3,
    },
  },
  bootstrapInput: {
    borderRadius: 6, 
    backgroundColor: "#6a3499",
    border: '1px solid #6a3499',
    fontSize: 16,
    width: "600px",
    padding: '10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 6,
      borderColor: '#6a3499',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
  bootstrapFormLabel: {
    fontSize: 20,
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20,

  },
  paper: {
    position: 'absolute',
    width: theme.spacing.unit * 50,
    backgroundColor: "#ffe8ff",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: 'none',
  },
  register: {
    fontSize: 15,
    fontFamily: "'Montserrat', sans-serif", 
    color: "#6a3499"
  }
});

class SignIn extends React.Component {
    state = {
        open: false,
        email: "",
        password: "",

      };

      handleOpen = () => {
        this.setState({ open: true });
      };

      handleClose = () => {
        this.setState({ open: false });
      };

      handleChange = (e) => {
          console.log(this.state);
          this.setState({
          [e.target.id]: e.target.value
          })
      }
      handleSubmit = (e) => {
          e.preventDefault();
          console.log(this.state);
      }

render(){ 
  const { classes } = this.props; 

  return (
      <div> 
    <div className="sticky-anchor"></div>
    <div className="sticky"> 

    <Card className={classes.card} >
      <CardContent className={classes.content}>
      <Typography   color="textSecondary" gutterBottom>
         Imaš li ideju za dijeljenje svojih tips'n'tricks?
        </Typography>
        <Typography className={classes.title} color="textSecondary" gutterBottom>
         Prijavi se ovdje!
        </Typography>
         <form onSubmit={this.handleSubmit}>
         <TextField
            name="email"
            id="email"
            type="email"
            autoComplete="email"
            label="Email"   
            className={classes.textField}
            onChange={this.handleChange}
            variant="outlined"
            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
         <TextField   
            id="password"
            label="Lozinka"  
            type= "password" 
            className={classes.textField}
            autoComplete="current-password"
            variant="outlined"
            onChange={this.handleChange}

            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
       <Button className={classes.login}>Ulogiraj se</Button>
             </form>
             <form > 
             <Typography  className={classes.reg} color="textSecondary" gutterBottom>
         Nemaš korisnički račun? registraj se <Button onClick={this.handleOpen} className={classes.register}>ovdje</Button>
        </Typography>

        <Modal
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div style={getModalStyle()} className={classes.paper}>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
            Registriraj se ovdje!
            </Typography>
          <TextField
            name="ime"
            label="Ime"   
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
         <TextField  name="prezime"
            label="Prezime"   
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
        <TextField
            name="email"
            label="Email"   
            type="email"
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
         <TextField  
            id="outlined-password-input"
            label="Lozinka"  
            type= "password" 
            className={classes.textField}
            autoComplete="current-password"
            variant="outlined"
            InputLabelProps={{
            classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused,
            },
            }}
            InputProps={{
            classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
            },
            }}
        /> <br/>
            <Button className={classes.login}>Registracija</Button>
          </div>
        </Modal>
        </form>
      </CardContent> 
    </Card>
    </div></div>
  );
}}

SignIn.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SignIn);
