import React, { Component } from "react";
import { NavLink, Link } from "react-router-dom";
import PropTypes from "prop-types";
import { getUser, logout } from "../Actions/UserActions";
import { withStyles } from "@material-ui/core/styles";
import { getPosts, savePost, deletePost } from "../Actions/PostActions";
import compose from "recompose/compose";
import { connect } from "react-redux";
import Modal from "@material-ui/core/Modal";
import CreateProject from "../Container/CreateProject";
import "./Navbar.css";
import QuizHome from "../Quiz/QuizHome";
import DSLR from "../Container/DSLR";
import MenuBar from "./MenuBar";
import ListPosts from "../Container/ListPosts";
function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`
  };
}
const styles = {
  root: {
    flexGrow: 1
  },
  button: {
    color: "#d6d7da",
    fontSize: 18,
    alignItems: "center",
    height: 50,
    fontFamily: "'Montserrat', sans-serif",
    cursor: "pointer"
  },
  tit: {
    textDecoration: "none",
    cursor: "pointer"
  },
  side: {
    marginTop: "-20%",
    marginLeft: "50%"
  },
  me: {
    padding: "50%"
  },
  link: {
    color: "black",
    textDecoration: "none"
  },
  fab: {
    backgroundColor: " rgb(115, 70, 124)",
    width: "200%",
    height: "100%",
    fontSize: 20,
    color: "white",
    alignItems: "center"
  },
  card: {
    backgroundColor: "#e0e0e0"
  },
  menu: {
    backgroundColor: " rgb(115, 70, 124)",
    cursor: "pointer"
  },
  paper: {
    position: "relative",
    width: "50%",
    backgroundColor: "white",
    padding: "2%",
    outline: "none"
  },
  modalStyle1: {
    overflow: "scroll",
    height: "100%",
    display: "block"
  },
  cursor: {
    cursor: "pointer"
  }
};

class NavbarPage extends Component {
  state = {
    open: false,
    isShowBody: true,
    anchorEl: null
  };
  handleClickQuiz(event) {
    this.setState({ isShowBody: !this.state.isShowBody });
  }
  handleOpen = () => {
    this.setState({ open: true });
  };
  handleClose = () => {
    this.setState({ anchorEl: null });
  };
  getUsername() {
    console.log(this.props.user.uid);
    console.log(this.props.user.email);
    var mail = this.props.user.email;
    var username = mail.substring(0, mail.indexOf("@"));
    localStorage.setItem("username", username);

    return username;
  }
  render() {
    const { classes } = this.props;
    return (
      <nav id="menu" className="menu " className="appbar">
        <Link to="/" className={classes.tit}>
          {" "}
          <button className="but-title">
            {" "}
            <h3 className="title">Kroz objektiv</h3>
          </button>{" "}
        </Link>
        <div className="dropdown">
          <button className="dropbtn">Tipovi fotoaparata</button>
          <div className="dropdown-content">
            <a href="/dslr">DSLR</a>
            <a href="#">Mirrorless</a>
            <a href="#">Kompaktni</a>
            <a href="#">Analogni</a>
            <a href="#">Canon</a>
            <a href="#">Nikon</a>
            <a href="#">Canon</a>
            <a href="#">Sony</a>
            <a href="#">Pentax</a>
          </div>
        </div>
        <div className="dropdown">
          <button className="dropbtn">Tips'n'tricks</button>
          <div className="dropdown-content">
            <a href="#">Sve</a>
            <a href="#">Kamera trikovi</a>
            <a href="#">Video trikovi</a>
            <a href="#">Editiranje</a>
          </div>
        </div>
        <div className="dropdown">
          <button className="dropbtn">Oprema</button>
          <div className="dropdown-content">
            <a href="#">Sve</a>
            <a href="#">Objektivi</a>
            <a href="#">Bljeskalice</a>
            <a href="#">Filteri</a>
          </div>
        </div>
        <div className="dropdown user-field">
          <button className="dropbtn">
            {" "}
            <i className="fas fa-camera" />
            <span className="user-name"> {this.getUsername()}</span>
          </button>
          <div className="dropdown-content">
            <div>
              <div onClick={this.handleClickQuiz.bind(this)}>
                <span>Quiz</span>
              </div>
              {this.state.isShowBody ? (
                <div className="row">
                  <ListPosts />
                </div>
              ) : (
                <div>
                  <QuizHome />
                </div>
              )}
            </div>
            <a onClick={this.handleOpen} className={classes.cursor}>
              Kreiraj članak
            </a>
            <Modal
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
              open={this.state.open}
              onClose={this.handleClose1}
              className={this.props.classes.modalStyle1}
            >
              <div style={getModalStyle()} className={classes.paper}>
                <CreateProject />
              </div>
            </Modal>{" "}
            <a
              onClick={() => {
                this.props.logout();
              }}
              className={classes.menu}
            >
              Izađi
            </a>
          </div>
        </div>
      </nav>
    );
  }
}
NavbarPage = connect(
  (state, ownProps) => ({
    posts: state.posts,
    user: state.user
  }),
  { savePost, getPosts, deletePost, getUser, logout }
)(NavbarPage);
NavbarPage.propTypes = {
  classes: PropTypes.object.isRequired
};
export default compose(withStyles(styles))(NavbarPage);
