import React from "react";
const Comment = props => {
  const { post } = this.props;
  return (
    <div className="mt-3">
      <h2>{post.name}</h2>
      <div className="card card-outline-secondard">
        <div className="card-block">
          {props.body}

          {props.delete && (
            <button
              className="btn btn-danger float-right"
              onClick={props.deleteComment}
            >
              Delete
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Comment;
