import React, { Component } from "react";
import { database } from "../Firebase";
import PostCard from "../Components/PostCard";
import ReactQuill from "react-quill";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import "../Layout/Navbar.css";
import TextField from "@material-ui/core/TextField";
import compose from "recompose/compose";
import { connect } from "react-redux";
import { getPosts, savePost, deletePost } from "../Actions/PostActions";
import { getUser, logout } from "../Actions/UserActions";
import Navbar from "../Layout/Navbar";
import MenuBar from "../Layout/MenuBar";
const styles = theme => ({
  root: {
    marginLeft: 5
  },
  head: {
    fontSize: 30,
    fontFamily: "sans-serif",
    textAlign: "center",
    textTransform: "uppercase",
    letterSpacing: 0.5,
    position: "absolute",
    color: "#c75b39",
    fontWeight: 900,
    marginLeft: 50,
    whiteSpace: "nowrap",
    marginTop: 10
  },
  container: {
    display: "flex",
    flexWrap: "wrap",
    paddingTop: 50,
    marginLeft: 5,
    paddingLeft: 40
  },
  link: {
    textDecoration: "none"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    marginTop: theme.spacing.unit,
    paddingBottom: 20,
    webkitAppearance: "none",
    mozAppearance: "none",
    appearance: "none",
    font: "15px/1  Open Sans , sans-serif",
    width: "500%",
    padding: "10px 11px 11px 11px"
  },

  cssLabel: {
    "&$cssFocused": {
      color: "black",
      fontSize: 20
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: "black"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: "black"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 3
    }
  },
  bootstrapInput: {
    borderRadius: 6,
    backgroundColor: "black",
    border: "1px solid black",
    fontSize: 16,
    width: "600px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: "black",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 20
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20
  },
  buttonPost: {
    margin: theme.spacing.unit,
    fontFamily: "sans-serif",
    textAlign: "center",
    textTransform: "uppercase",
    letterSpacing: 0.5,
    borderColor: "#D76000",
    color: "#EEEEEE",
    backgroundColor: "#D76000",
    fontSize: 20,
    width: 120,
    height: 50,
    marginLeft: 5
  },
  file: {
    margin: theme.spacing.unit,
    fontFamily: "sans-serif",
    textAlign: "center",
    textTransform: "uppercase",
    letterSpacing: 0.5,
    borderColor: "#D76000",
    color: "#EEEEEE",
    backgroundColor: "#D76000",
    fontSize: 12,
    width: 200,
    height: 50,
    marginBottom: 10
  }
});

class CreateProject extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      quill: "",
      naslovna: "",
      sazetak: "",
      signature: "",
      loading: false,
      posts: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleChangeNaslovna = this.handleChangeNaslovna.bind(this);
    this.handleChangeSazetak = this.handleChangeSazetak.bind(this);
    this.onHandleSubmit = this.onHandleSubmit.bind(this);
  }
  componentWillMount() {
    this.props.getPosts();
    this.props.getUser();
    if (
      this.props.user.loading === false &&
      this.props.user.email === undefined
    ) {
      this.props.history.replace("/Login");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (
      nextProps.user.loading === false &&
      nextProps.user.email === undefined
    ) {
      this.props.history.replace("/Login");
    }
  }
  componentDidMount() {
    database.on("value", snapshot => {
      this.setState({
        posts: snapshot.val(),
        loading: false
      });
    });
  }
  handleChange(values) {
    console.log(this.state);
    this.setState({ quill: values });
  }
  handleChangeNaslovna(values) {
    console.log(this.state);
    this.setState({ naslovna: values });
  }
  handleChangeSazetak(values) {
    console.log(this.state);
    this.setState({ sazetak: values });
  }
  onHandleSubmit(values) {
    console.log(this.state);
    values.preventDefault();
    const posts = {
      title: this.state.title,
      signature: this.state.signature,
      naslovna: this.state.naslovna,
      sazetak: this.state.sazetak,
      quill: this.state.quill
    };
    database.push(posts);
    this.setState({
      title: "",
      naslovna: "",
      sazetak: "",
      quill: "",
      signature: ""
    });
    this.props.history.push("/");
  }
  render() {
    const { classes } = this.props;
    return (
      <div>
        {" "}
        <Navbar />
        <MenuBar />
        <div className={classes.root}>
          <h4 className={classes.head}>Novi post</h4>
          <div className={classes.container}>
            <PostCard>
              <form onSubmit={this.onHandleSubmit} className="footerForm">
                <TextField
                  name="title"
                  label="Upiši naslov"
                  value={this.state.title}
                  onChange={values => {
                    this.setState({ title: values.target.value });
                  }}
                  className={classes.textField}
                  variant="outlined"
                  InputLabelProps={{
                    classes: {
                      root: classes.cssLabel,
                      focused: classes.cssFocused
                    }
                  }}
                  InputProps={{
                    classes: {
                      root: classes.cssOutlinedInput,
                      focused: classes.cssFocused,
                      notchedOutline: classes.notchedOutline,
                      input: classes.input
                    }
                  }}
                />{" "}
                <br />
                <ReactQuill
                  name="quill"
                  placeholder="Učitaj sliku za naslovnu"
                  value={this.state.naslovna}
                  modules={CreateProject.modulesNaslovna}
                  formats={CreateProject.formatsNaslovna}
                  onChange={this.handleChangeNaslovna}
                  className={classes.input}
                />{" "}
                <br />
                <ReactQuill
                  name="quill"
                  placeholder="Ukratko opišite o čemu Vaš post govori"
                  value={this.state.sazetak}
                  modules={CreateProject.modules}
                  formats={CreateProject.formats}
                  onChange={this.handleChangeSazetak}
                  className={classes.input}
                />{" "}
                <br />
                <ReactQuill
                  name="quill"
                  placeholder="Piši....briši(koristite slike manjeg formata)"
                  value={this.state.quill}
                  modules={CreateProject.modules}
                  formats={CreateProject.formats}
                  onChange={this.handleChange}
                  className={classes.input}
                />
                <TextField
                  name="signature"
                  label="Kreirao"
                  value={this.state.signature}
                  onChange={values => {
                    this.setState({ signature: values.target.value });
                  }}
                  className={classes.textField}
                  variant="outlined"
                  InputLabelProps={{
                    classes: {
                      root: classes.cssLabel,
                      focused: classes.cssFocused
                    }
                  }}
                />
                <button type="submit" className={classes.buttonPost}>
                  Kreiraj
                </button>
              </form>
            </PostCard>
          </div>
        </div>{" "}
      </div>
    );
  }
}
CreateProject.modules = {
  toolbar: [
    [{ header: "1" }, { header: "2" }],
    [{ size: [] }],
    ["bold", "italic", "underline", "strike", "blockquote"],
    [
      { list: "ordered" },
      { list: "bullet" },
      { indent: "-1" },
      { indent: "+1" }
    ],
    ["link", "image", "video"],
    ["clean"]
  ],
  clipboard: {
    // toggle to add extra line breaks when pasting HTML:
    matchVisual: false
  }
};

CreateProject.formats = [
  "header",
  "font",
  "size",
  "bold",
  "italic",
  "underline",
  "strike",
  "blockquote",
  "list",
  "bullet",
  "indent",
  "link",
  "image",
  "video"
];
CreateProject.modulesNaslovna = {
  toolbar: [["image"]],
  clipboard: {
    // toggle to add extra line breaks when pasting HTML:
    matchVisual: false
  }
};

CreateProject.formatsNaslovna = ["image"];

CreateProject.propTypes = {
  classes: PropTypes.object.isRequired
};

CreateProject = connect(
  (state, ownProps) => ({
    posts: state.posts,
    user: state.user
  }),
  { savePost, getPosts, deletePost, getUser, logout }
)(CreateProject);
CreateProject.propTypes = {
  classes: PropTypes.object.isRequired,
  width: PropTypes.string.isRequired
};
export default compose(withStyles(styles))(CreateProject);
