import React, { Component } from "react";
import { connect } from "react-redux";
import renderHTML from "react-render-html";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core";
import SubmitComment from "./SubmitComment";
import Navbar from "../Layout/Navbar";
import MenuBar from "../Layout/MenuBar";
const styles = {
  title: {
    marginLeft: 650,
    fontSize: 20,
    fontFamily: "'Lobster', cursive"
  },
  post: {
    marginLeft: 150,
    marginRight: 450,
    marginTop: 50,
    fontFamily: "'Montserrat', sans-serif"
  }
};
class PostDetail extends Component {
  renderComments() {
    const { post } = this.props;
    return _.map(post.comments, (comment, key) => {
      return (
        <Comment key={key} id={key}>
          {comment.content}
        </Comment>
      );
    });
  }
  render() {
    const { classes, post } = this.props;

    return (
      <div>
        <Navbar />
        <MenuBar />
        <Typography className={classes.title}>
          <h1 className="post-title">{post.title}</h1>
        </Typography>
        <Typography className={classes.post}>
          <span className="signature">
            Napisao : {renderHTML(post.signature)}
          </span>
        </Typography>
        <Typography className={classes.post}>
          <span className="post-quill">{renderHTML(post.quill)}</span>
        </Typography>
        {this.renderComments()}
      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return { post: state.posts[ownProps.match.params.id], uid: state.user.uid };
}

export default connect(mapStateToProps)(withStyles(styles)(PostDetail));
