          <div>
            <InputField
              id="email"
              type="text"
              label="Email"
              inputAction={event =>
                this.setState({ email: event.target.value })
              }
              style={this.state.error ? errStyle : null}
            />
            <InputField
              id="password"
              type="password"
              label="Password"
              inputAction={event =>
                this.setState({ password: event.target.value })
              }
              style={this.state.error ? errStyle : null}
            />
