import React, { Component } from "react";
import InputField from "../Components/InputField";
import FooterFormButton from "../Components/FooterFormButton";
import SimpleBox from "../Components/SimpleBox";
import { createAccount } from "../Actions/UserActions";
import { connect } from "react-redux";
import ErrorAlert from "../Components/ErrorAlert";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
const styles = theme => ({
  tit: {
    textDecoration: "none"
  },
  title: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif"
  },
  textField: {
    marginLeft: 30,
    width: 300,
    padding: "5%",
    margin: "5%"
  },
  pos: {
    marginBottom: 12
  },
  reg: {
    marginLeft: 35
  },
  cssLabel: {
    "&$cssFocused": {
      color: "#003300",
      fontSize: 20
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: "#003300"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: "#003300"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 3
    }
  },
  bootstrapInput: {
    borderRadius: 6,
    backgroundColor: "#003300",
    border: "1px solid #003300",
    fontSize: 16,
    width: "600px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: "#003300",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 20
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "white",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none"
  }
});

class CreateAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      confirmPassword: "",
      error: ""
    };
  }

  isValid() {
    const { email, password, confirmPassword } = this.state;

    if (email === "" || password === "" || confirmPassword === "") {
      this.setState({
        error: "Ispunite sva polja"
      });
      return false;
    }

    if (password !== confirmPassword) {
      this.setState({
        error: "Obje lozinke moraju biti iste."
      });
      return false;
    }

    return true;
  }

  /*submitAccount(event) {
    console.log(this.props);
    event.preventDefault();
    if (!this.isValid()) {
      return;
    }
    this.props
      .createAccount(this.state.email, this.state.password)
      .then(() => {
        this.props.history.replace("/");
      })
      .catch(err => {
        this.setState({
          error: err.message
        });
      });
  }*/
  onSubmit(data) {
    this.props.createAccount(data, this.state);
  }

  renderBody() {
    const errStyle = {
      borderColor: "red"
    };
    const { handleSubmit } = this.props;
    return (
      <div>
        <form onSubmit={handleSubmit(this.onSubmit.bind(this))}>
          <InputField
            name="email"
            type="text"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
          />
          <InputField
            name="fname"
            type="text"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
          />
          <InputField
            name="lname"
            type="text"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
          />
          <InputField
            name="password"
            type="password"
            label="Lozinka"
            inputAction={event =>
              this.setState({ password: event.target.value })
            }
            style={this.state.error ? errStyle : null}
          />

          {this.state.error && <ErrorAlert>{this.state.error}</ErrorAlert>}
          <FooterFormButton submitLabel="Registracija" {...this.props} />
        </form>
      </div>
    );
  }

  render() {
    return (
      <SimpleBox
        body={this.renderBody()}
        className="title"
        title="Kreiraj korisnički račun"
      />
    );
  }
}
CreateAccount.propTypes = {
  classes: PropTypes.object.isRequired
};

export default connect(
  null,
  { createAccount }
)(withStyles(styles)(CreateAccount));
