import React, { Component } from "react";
import { connect } from "react-redux";
import { getPosts, savePost, deletePost } from "../Actions/PostActions";
import { reduxForm, reset } from "redux-form";
import _ from "lodash";
import { getUser, logout } from "../Actions/UserActions";
import Link from "react-router-dom/es/Link";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import renderHTML from "react-render-html";
import compose from "recompose/compose";
import withWidth from "@material-ui/core/withWidth";
import "../Layout/Navbar.css";
import SignedInLinks from "../Dashboard/SignedInLinks";
import Navbar from "../Layout/Navbar";
import MenuBar from "../Layout/MenuBar";
const styles = theme => ({
  root: {
    flexGrow: 1
  },
  title: {
    left: 20,
    fontSize: 20,
    fontFamily: " 'Josefin Sans', sans-serif;"
  },
  col: {
    display: "flex",
    flexWrap: "wrap",
    marginLeft: 40,
    overflow: "hidden",
    flexDirection: "row",
    md: "auto",
    spacing: 0,
    actionPosition: "right",
    item: "true"
  },
  card: {
    maxWidth: 600,
    backgroundColor: "#e0e0e0",
    marginTop: -10
  },
  media: {
    verticalAlign: "middle",
    height: 200,
    top: 5
  },
  overlay: {
    position: "absolute",
    top: "160px",
    color: "black",
    backgroundColor: "rgba(176, 181, 177, 0.45)",
    width: 600
  },
  container: {
    display: "flex"
  }
});
class App extends Component {
  componentWillMount() {
    this.props.getPosts();
    this.props.getUser();
    if (
      this.props.user.loading === false &&
      this.props.user.email === undefined
    ) {
      this.props.history.replace("/Login");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (
      nextProps.user.loading === false &&
      nextProps.user.email === undefined
    ) {
      this.props.history.replace("/Login");
    }
  }

  renderPosts() {
    const { classes } = this.props;
    return _.map(this.props.posts, (post, key) => {
      return (
        <div className={classes.col}>
          <Link to={`/${key}`}>
            <div class="tile">
              <CardMedia>
                <img src={post.naslovna} />

                <div class="text">
                  <h1>{post.title}</h1>
                  <h2 class="animate-text">More lorem ipsum bacon ipsum.</h2>
                  <p class="animate-text">
                    Bacon ipsum dolor amet pork belly tri-tip turducken,
                    pancetta bresaola pork chicken meatloaf. Flank sirloin strip
                    steak prosciutto kevin turducken.{" "}
                  </p>
                  <div class="dots">
                    <span />
                    <span />
                    <span />
                  </div>
                </div>
              </CardMedia>
            </div>{" "}
          </Link>
        </div>
      );
    });
  }

  renderField(field) {
    return (
      <input
        type="text"
        placeholder={`Enter a ${field.label}...`}
        {...field.input}
        className={field.class}
      />
    );
  }
  onSubmit(values) {
    this.props.savePost(values).then(this.props.dispatch(reset("NewPost")));
  }

  render() {
    return (
      <div>
        <Navbar />
        <MenuBar />
        <div className="container">
          <div className="main">{this.renderPosts()}</div>
        </div>
        <div className="side">
          <SignedInLinks />
        </div>
      </div>
    );
  }
}

let form = reduxForm({
  form: "NewPost"
})(App);

form = connect(
  (state, ownProps) => ({
    posts: state.posts,
    user: state.user
  }),
  { savePost, getPosts, deletePost, getUser, logout }
)(form);
form.propTypes = {
  classes: PropTypes.object.isRequired,
  width: PropTypes.string.isRequired
};
export default compose(
  withStyles(styles),
  withWidth()
)(form);
