import React, { Component } from "react";
import { connect } from "react-redux";
import { saveUser } from "../Actions/UserProfile";
import { Field, reduxForm, reset } from "redux-form";
import _ from "lodash";
import PostCard from "../Components/PostCard";
import { getUser, logout } from "../Actions/UserActions";
import { Link } from "react-router-dom";
import { required } from "../Helpers/ReduxFormValidation";

class Username extends Component {
  renderPosts() {
    console.log(this.props.profile);
    return _.map(this.props.userProfile, (profile, key) => {
      return (
        <Link to={`/${key}`}>
          <div className="tile">
            <div className="text">
              <h1>{profile.name}</h1>
            </div>
          </div>{" "}
        </Link>
      );
    });
  }

  renderField(field) {
    const errStyle = {
      borderColor: "red"
    };
    return (
      <input
        type="text"
        placeholder={`Enter a ${field.label}...`}
        {...field.input}
        className={field.class}
        style={field.meta.touched && field.meta.error ? errStyle : null}
      />
    );
  }

  onSubmit(values) {
    this.props
      .saveUser(values, this.props.user.uid)
      .then(this.props.dispatch(reset("NewPost")));
  }

  render() {
    const { handleSubmit } = this.props;
    return (
      <div>
        <div className="container">
          <div className="main">User: {this.renderPosts()}</div>
          <div className="navbar fixed-bottom" />
          <form
            onSubmit={handleSubmit(this.onSubmit.bind(this))}
            className="footerForm"
          >
            <Field
              name="name"
              component={this.renderField}
              label="name"
              class="footer-title"
              validate={required}
            />
            <Field
              name="last"
              component={this.renderField}
              label="last"
              class="footer-body"
            />
            <button type="submit" className="btn footer-button">
              Post
            </button>
          </form>
        </div>
      </div>
    );
  }
}

let form = reduxForm({
  form: "NewProfile"
})(Username);

form = connect(
  (state, ownProps) => ({
    profile: state.profile,
    user: state.user
  }),
  { saveUser, getUser, logout }
)(form);

export default form;
