import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import SimpleBox from "../Components/SimpleBox";
import FooterFormButton from "../Components/FooterFormButton";
import { login, getUser } from "../Actions/UserActions";
import { connect } from "react-redux";
import _ from "lodash";
import ErrorAlert from "../Components/ErrorAlert";
import "../Layout/Navbar.css";
import Home from "../Container/Home";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import Modal from "@material-ui/core/Modal";
import CreateAccount from "../Container/CreateAccount";
function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`
  };
}
const styles = theme => ({
  card: {
    width: 490,
    height: 320,
    marginLeft: 600,
    marginTop: "-45%"
  },

  tit: {
    textDecoration: "none"
  },
  title: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif"
  },
  textField: {
    marginLeft: 30,
    width: 240,
    padding: 5
  },
  pos: {
    marginBottom: 12
  },
  reg: {
    marginLeft: 35
  },
  login: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
    marginLeft: 30,
    color: " #003300"
  },
  cssLabel: {
    "&$cssFocused": {
      color: " #003300",
      fontSize: 20
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: " #003300"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: " #003300"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 3
    }
  },
  bootstrapInput: {
    borderRadius: 6,
    backgroundColor: " #003300",
    border: "1px solid  #003300",
    fontSize: 16,
    width: "600px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: " #003300",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 20
  },
  dense: {
    marginTop: 25
  },
  input: {
    marginLeft: 5,
    fontSize: 20
  },
  paper: {
    position: "absolute",
    width: theme.spacing.unit * 50,
    backgroundColor: "#757e75e1 !important",
    boxShadow: theme.shadows[5],
    padding: theme.spacing.unit * 4,
    outline: "none"
  },
  register: {
    fontSize: 15,
    fontFamily: "'Montserrat', sans-serif",
    color: " #003300"
  }
});
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      email: "",
      password: "",
      error: ""
    };
  }
  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };
  componentWillMount() {
    this.props.getUser();
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.user.email !== undefined) {
      this.props.history.push("/");
    }
  }

  submitLogin(event) {
    event.preventDefault();
    this.props.login(this.state.email, this.state.password).catch(err => {
      this.setState({
        error: err
      });
    });
  }

  renderBody() {
    const errStyle = {
      borderColor: "red"
    };
    const { classes } = this.props;
    return (
      <div className="bg-text">
        <form
          onSubmit={event => {
            this.submitLogin(event);
          }}
        >
          <Typography
            className={classes.title}
            color="textSecondary"
            gutterBottom
          >
            Prijavi se ovdje!
          </Typography>
          <TextField
            name="email"
            id="email"
            type="text "
            autoComplete="email"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
            className={classes.textField}
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
          />{" "}
          <br />
          <TextField
            id="password"
            label="Lozinka"
            type="password"
            inputAction={event =>
              this.setState({ password: event.target.value })
            }
            style={this.state.error ? errStyle : null}
            className={classes.textField}
            autoComplete="current-password"
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
          />{" "}
          <br />
          {this.state.error && (
            <ErrorAlert>Your username/password is incorrect</ErrorAlert>
          )}{" "}
          <br />
          <FooterFormButton
            className="footer-form"
            submitLabel="Ulogiraj se"
            {...this.props}
          />
        </form>
        <Button onClick={this.handleOpen} className="submit-label">
          Registriraj se
        </Button>
        <Modal
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
          open={this.state.open}
          onClose={this.handleClose}
        >
          <div style={getModalStyle()} className={classes.paper}>
            <CreateAccount />
          </div>
        </Modal>
      </div>
    );
  }

  render() {
    return (
      <div>
        <Home />
        <SimpleBox body={this.renderBody()} />
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { user: state.user };
}
Login.propTypes = {
  classes: PropTypes.object.isRequired
};
export default connect(
  mapStateToProps,
  { login, getUser }
)(withStyles(styles)(Login));
