import React, { Component } from "react";
import { connect } from "react-redux";
import Link from "react-router-dom/es/Link";
import { deletePost, deleteComment } from "../Actions/PostActions";
import AddComment from "./AddComment";
import Comment from "../Components/Comment";
import _ from "lodash";
import renderHTML from "react-render-html";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core";
import Navbar from "../Layout/Navbar";

class PostDetail extends Component {
  renderComments() {
    return _.map(this.props.post.comments, (comment, key) => {
      return (
        <div>
          <Comment
            key={key}
            head={comment.name}
            body={comment.content}
            id={key}
            delete={comment.uid === this.props.user.uid}
            deleteComment={() => {
              this.props.deleteComment(this.props.match.params.id, key);
            }}
          />
        </div>
      );
    });
  }

  componentWillMount() {
    const { post, history } = this.props;
    if (post === undefined || post === null) {
      history.replace("/");
    }
  }

  render() {
    const { classes, post, history, match, deletePost, user } = this.props;

    if (!post) return null;

    return (
      <div>
        {" "}
        <Navbar />
        <div lassName="container-fluid">
          <div lassName="row">
            <div lassName="col-lg-4 col-md-6 col-sm-12">
              <div lassName="card">{renderHTML(post.naslovna)}</div>
            </div>
          </div>
        </div>
        <Typography>
          <h1 className="post-title">{post.title}</h1>
        </Typography>
        <div className="subject">
          <Typography>
            <span className="signature">Napisao : {post.signature}</span>
          </Typography>
          <Typography>
            <span className="post-quill">{renderHTML(post.quill)}</span>

            {post.uid === user.uid && (
              <button
                className="button-delete"
                onClick={() => {
                  deletePost(match.params.id);
                  history.push("/");
                }}
              >
                Izbriši post
              </button>
            )}
          </Typography>{" "}
        </div>{" "}
        <div className="horizontal-line">
          Najnoviji komentari:
          <hr />
        </div>
        <div className="comment">
          <AddComment id={this.props.match.params.id} />
          {this.renderComments()}
        </div>
      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return {
    post: state.posts[ownProps.match.params.id],
    user: state.user,
    posts: state.posts
  };
}

export default connect(
  mapStateToProps,
  { deletePost, deleteComment }
)(PostDetail);

