import React, { Component } from "react";
import SimpleBox from "../Components/SimpleBox";
import InputField from "../Components/InputField";
import FooterFormButton from "../Components/FooterFormButton";
import { login, getUser, createAccount } from "../Actions/UserActions";
import { connect } from "react-redux";
import ErrorAlert from "../Components/ErrorAlert";
import Home from "../Container/Home";
import CreateAccount from "../Container/CreateAccount";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Modal from "@material-ui/core/Modal";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import { TextField } from "material-ui/core/TextField";

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`
  };
}
const styles = theme => ({
  card: {
    width: 490,
    height: 320,
    marginLeft: 600,
    marginTop: "-45%"
  },
  racun: {
    fontFamily: "'Montserrat', sans-serif"
  },
  paper: {
    position: "absolute",
    width: 550,
    backgroundColor: "white",
    borderRadius: "5px",
    outline: "2px solid rgb(83, 53, 88)"
  },
  register: {
    backgroundColor: "rgb(83, 53, 88)",
    fontSize: 18,
    height: "2.5em",
    width: "50%",
    color: "white",
    fontFamily: "'Montserrat', sans-serif",
    padding: "2%"
  },
  textField: {
    marginLeft: 30,
    width: 340,
    padding: 5
  },
  pos: {
    marginBottom: 12
  },
  reg: {
    marginLeft: 35
  },
  login: {
    fontSize: 20,
    fontFamily: "'Montserrat', sans-serif",
    marginLeft: 30,
    color: "rgb(83, 53, 88)"
  },
  cssLabel: {
    "&$cssFocused": {
      color: "rgb(83, 53, 88)",
      fontSize: 18
    }
  },
  cssFocused: {},
  cssUnderline: {
    "&:after": {
      borderBottomColor: "rgb(83, 53, 88)"
    }
  },
  cssOutlinedInput: {
    "&$cssFocused $notchedOutline": {
      borderColor: "rgb(83, 53, 88)"
    }
  },
  notchedOutline: {},
  bootstrapRoot: {
    "label + &": {
      marginTop: theme.spacing.unit * 2
    }
  },
  bootstrapInput: {
    marginLeft: 20,
    borderRadius: 6,
    backgroundColor: "rgb(83, 53, 88)",
    border: "1px solid rgb(83, 53, 88)",
    fontSize: 16,
    width: "700px",
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"'
    ].join(","),
    "&:focus": {
      borderRadius: 6,
      borderColor: "rgb(83, 53, 88)",
      boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)"
    }
  },
  bootstrapFormLabel: {
    fontSize: 18
  },
  dense: {
    marginTop: 20
  },
  input: {
    marginLeft: 5,
    fontSize: 18
  }
});
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      error: "",
      message: localStorage.getItem("message") || ""
    };
  }
  handleClose = () => {
    this.setState({ open: false });
  };

  handleOpen = () => {
    this.setState({ open: true });
  };
  componentWillMount() {
    if (this.props.user !== null) {
      this.props.history.push("/");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.user !== null) {
      nextProps.history.push("/");
    }
  }

  submitLogin(event) {
    event.preventDefault();
    this.props.login(this.state.email, this.state.password).catch(err => {
      this.setState({
        error: err
      });
    });
  }
  onChange = event => {
    const message = event.target.value;

    localStorage.setItem("message", message);
    this.setState({ message });
  };
  renderBody() {
    const errStyle = {
      borderColor: "red"
    };
    const { classes } = this.props;
    return (
      <form
        onSubmit={event => {
          this.submitLogin(event);
        }}
      >
        <div className="bg-text">
          <TextField
            className={classes.textField}
            autoComplete="current-password"
            variant="outlined"
            InputLabelProps={{
              classes: {
                root: classes.cssLabel,
                focused: classes.cssFocused
              }
            }}
            InputProps={{
              classes: {
                root: classes.cssOutlinedInput,
                focused: classes.cssFocused,
                notchedOutline: classes.notchedOutline,
                input: classes.input
              }
            }}
            value={this.state.message}
            onChange={this.onChange}
          />
          <InputField
            id="email"
            type="text"
            label="Email"
            inputAction={event => this.setState({ email: event.target.value })}
            style={this.state.error ? errStyle : null}
          />
          <InputField
            id="password"
            type="password"
            label="Lozinka"
            inputAction={event =>
              this.setState({ password: event.target.value })
            }
            style={this.state.error ? errStyle : null}
          />
          {this.state.error && (
            <ErrorAlert>Your username/password is incorrect</ErrorAlert>
          )}
          <FooterFormButton submitLabel="Prijava" {...this.props} />
          <p className={classes.racun}>Nemaš korisnički račun?</p>
          <Button onClick={this.handleOpen} className={classes.register}>
            Registriraj se
          </Button>
          <Modal
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={this.state.open}
            onClose={this.handleClose}
          >
            <div style={getModalStyle()} className={classes.paper}>
              <CreateAccount />
            </div>
          </Modal>{" "}
        </div>
      </form>
    );
  }

  render() {
    return (
      <div>
        <Home />
        {this.renderBody()}
      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return { user: state.user };
}
Login.propTypes = {
  classes: PropTypes.object.isRequired
};
export default connect(
  mapStateToProps,
  { login, getUser }
)(withStyles(styles)(Login));

