import { database_user } from "../Firebase";
export const FETCH_USER_PROFILE = "fetch_user_profile";
export const POST_PROFILE = "post_profile";

export function getPosts() {
  return dispatch => {
    dispatch({
      type: POST_PROFILE,
      payload: true
    });
    database_user.on(
      "value",
      snapshot => {
        dispatch({
          type: FETCH_USER_PROFILE,
          payload: snapshot.val()
        });
        dispatch({
          type: POST_PROFILE,
          payload: false
        });
      },
      () => {
        dispatch({
          type: POST_PROFILE,
          payload: -1
        });
      }
    );
  };
}

export function saveUser(userProfile, uid) {
  return dispatch => database.push({ ...userProfile, uid });
}

