    flexDirection: "row",
    md: "auto",
    spacing: 0,
    actionPosition: "right",
    item: "true"
  },
  card: {
    maxWidth: 600,
    backgroundColor: "#e0e0e0",
    marginTop: -10
  },
  media: {
    verticalAlign: "middle",
    height: 200,
    top: 5
  },
  overlay: {
    position: "absolute",
