import React, { Component } from "react";
import { connect } from "react-redux";
import { getPosts, savePost, deletePost } from "../Actions/PostActions";
import { reduxForm, reset } from "redux-form";
import _ from "lodash";
import Link from "react-router-dom/es/Link";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import renderHTML from "react-render-html";
import compose from "recompose/compose";
import withWidth from "@material-ui/core/withWidth";
import "../Layout/Navbar.css";

const styles = theme => ({
  root: {
    flexGrow: 1
  },
  title: {
    left: 20,
    fontSize: 20,
    fontFamily: " 'Josefin Sans', sans-serif;"
  },
  col: {
    display: "flex",
    flexWrap: "wrap",
    marginLeft: 40,
    overflow: "hidden",
    flexDirection: "row",
    md: "auto",
    spacing: 0,
    actionPosition: "right",
    item: "true"
  },
  card: {
    maxWidth: 600
  },
  media: {
    verticalAlign: "middle",
    height: 200,
    marginTop: 15
  },
  overlay: {
    position: "absolute",
    top: "160px",
    color: "black",
    backgroundColor: "rgba(176, 181, 177, 0.45)",
    width: 600
  },
  container: {
    display: "flex"
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: "center",
    color: theme.palette.text.secondary,
    flex: "1 0 auto",
    margin: theme.spacing.unit
  }
});
class App extends Component {
  componentWillMount() {
    this.props.getPosts();
  }
  renderPosts() {
    const { classes } = this.props;
    return _.map(this.props.posts, (post, key) => {
      return (
        <div className="row">
          <div className={classes.col}>
            <Link to={`/${key}`}>
              <Card className={classes.card}>
                <CardActionArea className={classes.actionarea}>
                  <CardMedia className={classes.media} height="100" width="300">
                    <span className="post-naslovna">
                      {renderHTML(post.naslovna)}
                    </span>
                    <CardContent className={classes.overlay}>
                      {<span className={classes.title}>{post.title} </span>}
                    </CardContent>
                  </CardMedia>
                </CardActionArea>
              </Card>
            </Link>
          </div>
        </div>
      );
    });
  }

  renderField(field) {
    return (
      <input
        type="text"
        placeholder={`Enter a ${field.label}...`}
        {...field.input}
        className={field.class}
      />
    );
  }
  onSubmit(values) {
    this.props.savePost(values).then(this.props.dispatch(reset("NewPost")));
  }

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <div className="container">
          <div className="row">
            <div className="col-10 right">{this.renderPosts()}</div>
          </div>
        </div>
      </div>
    );
  }
}

let form = reduxForm({
  form: "NewPost"
})(App);

form = connect(
  (state, ownProps) => ({
    posts: state.posts
  }),
  { savePost, getPosts, deletePost }
)(form);
form.propTypes = {
  classes: PropTypes.object.isRequired,
  width: PropTypes.string.isRequired
};
export default compose(
  withStyles(styles),
  withWidth()
)(form);

