import React from "react";

const Loading = () => {
  return (
    <div className="align-self-center">
      <i className="fa fa-refresh fa-spin fa-3x fa-fw loading" />
      <span className="sr-only">Loading...</span>
    </div>
  );
};

export default Loading;
