    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <div className="navbar">
          <button
            className="btn btn-danger"
            onClick={() => {
              this.props.logout();
            }}
          >
            Sign out
          </button>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-10 right">{this.renderPosts()}</div>
          </div>
        </div>
      </div>
    );
  }
}

