import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles'; 
import {Link} from "react-router-dom"  
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import "../Layout/Navbar.css";

import PopupState, {
    bindTrigger,
    bindMenu
  } from "material-ui-popup-state/index";
  import "./Navbar.css";

  const styles  =  ( {
    root: {
      flexGrow: 1
    }, 
    tit: {
        textDecoration: "none", 
    },
    item: {
        fontFamily: "'Montserrat', sans-serif",
        backgroundColor: "grey",
        height: 20,
    }

  })


class MenuBar extends React.Component {

  render() {
    const { classes } = this.props;


    return (
        <nav id="menu" class="menu"> 
        <Link to="/" className={classes.tit}><PopupState variant="popover" popupId="demo-popup-menu">
          {popupState => (
            <React.Fragment>
              <Button
                variant="contained"
                {...bindTrigger(popupState)} 
                className="button"
              >
                Tipovi fotoaparata
              </Button>
              <Menu {...bindMenu(popupState)}>
                <MenuItem onClick={popupState.close} className={classes.item}>DSLR</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Kompaktni</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Analogni</MenuItem>
              </Menu>
            </React.Fragment>
          )}
        </PopupState> </Link>  
        <Link to="/"className={classes.tit}><PopupState variant="popover" popupId="demo-popup-menu">
          {popupState => (
            <React.Fragment>
              <Button
                variant="contained"
                {...bindTrigger(popupState)} 
              >
                 Marke fotoaparata
              </Button>
              <Menu {...bindMenu(popupState)}>
                <MenuItem onClick={popupState.close} className={classes.item}>Canon</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Nikon</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Sony</MenuItem>
              </Menu>
            </React.Fragment>
          )}
        </PopupState> </Link> 
        <Link to="/"className={classes.tit}><PopupState variant="popover" popupId="demo-popup-menu">
          {popupState => (
            <React.Fragment>
              <Button
                variant="contained"
                {...bindTrigger(popupState)} 
              >
                Tips'n'Tricks
              </Button>
              <Menu {...bindMenu(popupState)}>
                <MenuItem onClick={popupState.close} className={classes.item}>Sve</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Kamera trikovi</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Video trikovi</MenuItem>
                <MenuItem onClick={popupState.close} className={classes.item}>Editiranje</MenuItem>
              </Menu>
            </React.Fragment>
          )}
        </PopupState> </Link> 
        <PopupState variant="popover" popupId="demo-popup-menu">
          {popupState => (
            <React.Fragment>
              <Button
                variant="contained"
                {...bindTrigger(popupState)}

              >
                Oprema
              </Button>
            </React.Fragment>
          )}
        </PopupState>

    </nav>

    );
  }
}
MenuBar.propTypes = {
    classes: PropTypes.object.isRequired
  };

export default withStyles(styles, { withTheme: true })(MenuBar);

