import { database_user } from "../Firebase";

export const FETCH_USER = "fetch_users";
export const USER_PROFILE = "user_profile";
export function getProfile() {
  return dispatch => {
    dispatch({
      type: USER_PROFILE,
      payload: true
    });
    database_user.on(
      "value",
      snapshot => {
        dispatch({
          type: FETCH_USER,
          payload: snapshot.val()
        });
        dispatch({
          type: USER_PROFILE,
          payload: false
        });
      },
      () => {
        dispatch({
          type: USER_PROFILE,
          payload: -1
        });
      }
    );
  };
}

export function saveProfiles(profile, uid) {
  return dispatch => database_user.push({ ...profile, uid });
}
